var dbUri = "jdbc:jtds:sqlserver://127.0.0.1:1433/";
var sqldb_name = "mmslogkms";
var sqldb_user = "sa";
var sqldb_password = "Admin123";
var sqldb_instance=";INSTANCE=SQLEXPRESS";
var tam_producto = 8; //ojo configurarlo para cada instalaci贸n
var tam_cod_almacen = 4; // ojo configurarlo para cada instalacion
var esReponedor = '0';



// compruebo si el c贸digo es de un reponedor
var resultQuery = "select  COUNT(*) from USUARIO as u inner join USUARIO_PERFIL_USUARIO as upu on upu.usuario_id=u.usuario_id inner join PERFIL_USUARIO as pu on pu.perfil_id=upu.perfil_id where u.cod_tag='"+$('producto')+"'";
var dbConn = DatabaseConnectionFactory.createDatabaseConnection ("net.sourceforge.jtds.jdbc.Driver", dbUri + sqldb_name, sqldb_user, sqldb_password);
var result = dbConn.executeCachedQuery(resultQuery);
logger.info("Reponedor query: "+resultQuery);
dbConn.close();

while(result.next()){
		esReponedor = result.getString(1);
		logger.info("rows: "+esReponedor);
}

// compruebo si el c贸digo pertenece a un usuario para eliminar los pedidos en reposcion
if (esReponedor=='1'){
	logger.info("Reponedor...");
	// llamo al procedimiento que eliminar los articulos en reposicion en el citado almacen y servicio, ojo!!! solo en estos
	// INSERTAMOS LA SOLICITUD
		var params = new java.util.ArrayList();;
		params.add($('almacen'));
		var sql = "EXEC EliminaPedidos ?";
		var dbConn = DatabaseConnectionFactory.createDatabaseConnection ("net.sourceforge.jtds.jdbc.Driver", dbUri + sqldb_name, sqldb_user, sqldb_password);
		var result = dbConn.executeUpdate(sql, params);
		dbConn.close();
	channelMap.put('response', "Reposici贸n informada");
	channelMap.put('estadoPedido', "correctamente");
	// **********************************************************************************************
} else{ // el tag no es de un reponedor, comprobamos si es puede realizar la solicitud
	var resultQuery = "SELECT * FROM CONTENEDOR WHERE cod_contenedor='"+$('producto')+"'";

	var dbConn = DatabaseConnectionFactory.createDatabaseConnection ("net.sourceforge.jtds.jdbc.Driver", dbUri + sqldb_name, sqldb_user, sqldb_password);
	var result = dbConn.executeCachedQuery(resultQuery);
	dbConn.close();

	// obtenemos los datos de la tabla CONTENEDOR pata generar la reposicion
	if (result.next())
	{
		var cod_tag=result.getString(4);
		var cant_reponer=result.getString(6);
	}

	logger.info("contenedor: "+resultQuery);


	// Obtenemos ahora los datos de la tabla PRODUCTO para presentar en pantalla la descripci贸n del articulo
	// extraigo el codigo de prodcuto desde el codigo de barras leido
	logger.info("cod_producto: "+$('producto').length());
	var cod_producto=$('producto').substring(tam_cod_almacen, $('producto').length()-1);
	logger.info("producto: "+cod_producto);
	var resultQuery = "SELECT * FROM PRODUCTO WHERE cod_producto='"+cod_producto+"'";
	logger.info("producto query: "+resultQuery);
	var dbConn = DatabaseConnectionFactory.createDatabaseConnection ("net.sourceforge.jtds.jdbc.Driver", dbUri + sqldb_name, sqldb_user, sqldb_password);
	var result = dbConn.executeCachedQuery(resultQuery);
	dbConn.close();

	if (result.next())
	{
		var descripcion=result.getString(3);
		logger.info("descripcion: "+descripcion);
		channelMap.put('response', descripcion);
		channelMap.put('estadoPedido', "");
	}

	// Realizamos la resposici贸n  del articulo
	var resultQuery = "SELECT * FROM MAPA_REPOSICIONES WHERE cod_tag='"+cod_tag+"' AND (estado_reposicion='PENDIENTE' or estado_reposicion='ENVIADO' or estado_reposicion='FINALIZADO')";

	var dbConn = DatabaseConnectionFactory.createDatabaseConnection ("net.sourceforge.jtds.jdbc.Driver", dbUri + sqldb_name, sqldb_user, sqldb_password);
	var result = dbConn.executeCachedQuery(resultQuery);
	dbConn.close();
	// si tenemos articulos pendientes no hacemos nada, en caso contrario realizamos una solicitud
	result.last();

	if(result.getRow()>0){
		channelMap.put('estadoPedido', "Error!!!\x0dArt铆culo ya solicitado");
	}else{
		// INSERTAMOS LA SOLICITUD
		var params = new java.util.ArrayList();;
		params.add($('producto'));
		logger.info("Solicitado: "+$('producto'));
		var sql = "EXEC Inserta_Pedido ?";
		var dbConn = DatabaseConnectionFactory.createDatabaseConnection ("net.sourceforge.jtds.jdbc.Driver", dbUri + sqldb_name, sqldb_user, sqldb_password);
		var result = dbConn.executeUpdate(sql, params);
		dbConn.close();

		channelMap.put('estadoPedido', "Articulo solicitado");
	}
}

